import { BGPNeighbour } from './bgp-neighbour';
import { RoutingBGPConfigJson } from './__json__/routing-bgp-config-json';
import { Redistribution } from './redistribution';

/**
 * Routing BGP Configuration
 */
/* istanbul ignore next: autogenerated */
export class RoutingBGPConfig {

  constructor(private _json: RoutingBGPConfigJson) {
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get local as.
   * @returns {number}
   */
  get localAs(): number {
    return this._json.local_as;
  }

  /**
   * Get local as number.
   * @returns {string}
   */
  get localAsNumber(): string {
    return this._json.local_as_number;
  }

  /**
   * Get bgp neighbours.
   * @returns {Array<BGPNeighbour>}
   */
  get bgpNeighbours(): Array<BGPNeighbour> {
    return this._json.bgp_neighbours.map(it => new BGPNeighbour(it));
  }

  /**
   * Get redistribution.
   * @returns {Redistribution | undefined}
   */
  get redistribution(): Redistribution | undefined {
    return this._json.redistribution
      ? new Redistribution(this._json.redistribution)
      : undefined;
  }

  /**
   * Get graceful restart.
   * @returns {boolean}
   */
  get gracefulRestart(): boolean {
    return this._json.graceful_restart;
  }

  /**
   * Get default originate.
   * @returns {boolean}
   */
  get defaultOriginate(): boolean {
    return this._json.default_originate;
  }

  /**
   * Get the json representation of this class.
   * @returns {RoutingBGPConfigJson}
   */
  get json(): RoutingBGPConfigJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
