import { VappIntegratedBackupStatusJson } from './__json__/vapp-integrated-backup-status-json';
import { IntegratedBackupStatus } from './__json__/integrated-backup-status';
import { VmIntegratedBackupStatus } from './vm-integrated-backup-status';

/**
 * vApp Integrated Backup Status.
 */
/* istanbul ignore next: autogenerated */
export class VappIntegratedBackupStatus {

  constructor(private _intBackupStatusJson: VappIntegratedBackupStatusJson) {
  }

  /**
   * Get the UUID of the vApp.
   * @returns {string}
   */
  get vappUuid(): string {
    return this._intBackupStatusJson.vapp_uuid;
  }

  /**
   * Get the vApp's backup configuration status.
   * @returns {IntegratedBackupStatus}
   */
  get status(): IntegratedBackupStatus {
    return this._intBackupStatusJson.status;
  }

  /**
   * Get the statuses of child VMs.
   * @returns {Array<VmIntegratedBackupStatus>}
   */
  get vmStatuses() {
    return this._intBackupStatusJson.vm_statuses.map(it => new VmIntegratedBackupStatus(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {VappIntegratedBackupStatusJson}
   */
  get json(): VappIntegratedBackupStatusJson {
    return Object.assign({}, this._intBackupStatusJson);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._intBackupStatusJson, undefined, 2);
  }
}
